mapper.update:
UPDATE boards

	board_title = #{boardTitle},
	board_cont = #{boardContent},
	board_cat = #{boardCategory}

WHERE board_no = #{boardNo}

mapper.select:
SELECT COUNT(*)
FROM( SELECT *
FROM boards
  
  
  rcp_no like '%${searchKeyword}%'  
  nickname in #{searchKeyword}  
  rcp_title like '%${searchKeyword}%'  
  
 
WHERE board_cat = #{searchCategory}
) countTable
