mapper.update:
UPDATE stars
set
star_cnt=#{star}
WHERE rcp_no=#{recipeNo} AND nickname LIKE #{nickname}

mapper.select:
SELECT inner.rcp_no, inner.nickname, inner.rcp_title, inner.rcp_img_path, inner.rcp_cnt ,innerR.rcp_cont FROM( SELECT inner_table.rcp_no, inner_table.nickname, inner_table.rcp_title, inner_table.rcp_img_path, inner_table.rcp_cnt, ROWNUM AS row_seq FROM ( SELECT DISTINCT re.rcp_no, re.nickname, re.rcp_title, re.rcp_img_path, re.rcp_cnt, re.rcp_date FROM recipes re, (SELECT rc.RCP_NO iRcpNo, i.INGREDIENT_NAME ingName FROM ingredients i, rcp_ing rc WHERE i.INGREDIENT_NO = rc.INGREDIENT_NO )ingre
(ingre.ingName LIKE #{searchKeyword} AND ingre.iRcpNo =re.rcp_no) OR re.rcp_title like '%${searchKeyword}%' OR re.nickname like '%${searchKeyword}%'
ORDER BY
re.rcp_date DESC
re.rcp_cnt DESC
) inner_table WHERE ROWNUM <= #{endRowNum} ) inner ,recipes innerR WHERE inner.rcp_no = innerR.rcp_no AND row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT inner.rcp_no FROM (SELECT rcp_no,count(*) AS firstRCP FROM rcp_ing WHERE ingredient_no IN (SELECT ingredient_no FROM ingredients WHERE ingredient_name IN
#{item}
) GROUP BY rcp_no ORDER BY firstRCP DESC) inner

SELECT COUNT(*) FROM ( SELECT DISTINCT re.rcp_no FROM recipes re , (SELECT rc.RCP_NO iRcpNo, i.INGREDIENT_NAME ingName FROM ingredients i, rcp_ing rc WHERE i.INGREDIENT_NO = rc.INGREDIENT_NO )ingre
(ingre.ingName LIKE #{searchKeyword} AND ingre.iRcpNo =re.rcp_no) OR re.rcp_title like '%${searchKeyword}%' OR re.nickname like '%${searchKeyword}%'
) countTable
